mapper.insert:
INSERT INTO 
TB_BBS_MANAGE (
  BM_INNB
, BM_CODE
, BM_ALL_NTCAT
, BM_UPEND_AT
, BM_SJ
, BM_CN
, BM_REGIST_IP
, DELETE_AT
, REGIST_MAN
, REGIST_DE
, UPDT_MAN
, UPDT_DE
)
VALUES (
  #{bm_innb}
, #{bm_code}
, #{bm_all_ntcat, jdbcType=VARCHAR}
, #{bm_upend_at, jdbcType=VARCHAR}
, #{bm_sj}
, #{bm_cn}
, #{bm_regist_ip, jdbcType=VARCHAR}		
, 'N'
, #{regist_man}
, SYSDATE
, #{regist_man}
, SYSDATE
)

mapper.update:
UPDATE TB_BBS_MANAGE
  SET BM_ALL_NTCAT = #{bm_all_ntcat,jdbcType=VARCHAR} 
	, BM_UPEND_AT  = #{bm_upend_at,jdbcType=VARCHAR}  
	, BM_SJ        = #{bm_sj}        
	, BM_CN        = #{bm_cn}        
	, UPDT_MAN     = #{regist_man,jdbcType=VARCHAR}   
	, UPDT_DE      = SYSDATE   
WHERE BM_INNB = #{bm_innb}

