mapper.select:
SELECT COUNT(*) cnt FROM tb_plan_rnd_day_item PRDI JOIN tb_str_manage SM ON PRDI.prdi_sm_code = SM.sm_code AND SM.sm_cvscafe_at = 'Y' JOIN tb_orgnzt_manage OM ON PRDI.om_code = OM.om_code JOIN tb_empl_manage EM ON PRDI.regist_man = EM.em_no LEFT OUTER JOIN tb_diary_rnd_cvs DRC ON PRDI.plan_de = DRC.base_de AND PRDI.regist_man = DRC.regist_man WHERE PRDI.delete_at = 'N'
AND PRDI.prdi_sm_code_nm LIKE '%${params.smNm}%'
AND EM.em_nm LIKE '%${params.emNm}%'
AND PRDI.om_code = #{params.omCode}
AND EM.team_code = #{params.tmCode}
AND PRDI.plan_de >= #{params.startDate}
AND PRDI.plan_de <= #{params.endDate}

SELECT DRC.drc_innb , OM.om_nm , EM.em_nm , SM.sm_nm , DATE_FORMAT(PRDI.plan_de,'%Y-%m-%d') plan_de , IF(DRC.drc_innb IS NULL , 'N' ,'Y') cvs_status FROM tb_plan_rnd_day_item PRDI JOIN tb_str_manage SM ON PRDI.prdi_sm_code = SM.sm_code AND SM.sm_cvscafe_at = 'Y' JOIN tb_orgnzt_manage OM ON PRDI.om_code = OM.om_code JOIN tb_empl_manage EM ON PRDI.regist_man = EM.em_no LEFT OUTER JOIN tb_diary_rnd_cvs DRC ON PRDI.plan_de = DRC.base_de AND PRDI.regist_man = DRC.regist_man AND PRDI.prdi_sm_code = DRC.sm_code WHERE PRDI.delete_at = 'N'
AND PRDI.prdi_sm_code_nm LIKE '%${params.smNm}%'
AND EM.em_nm LIKE '%${params.emNm}%'
AND PRDI.om_code = #{params.omCode}
AND EM.team_code = #{params.tmCode}
AND PRDI.plan_de >= #{params.startDate}
AND PRDI.plan_de <= #{params.endDate}
ORDER BY PRDI.plan_de DESC ${pagingEnd}

	SELECT drc_partclr_matter AS partclr_matter
	  FROM  tb_diary_rnd_cvs  
  	 WHERE drc_innb = #{drc_innb}
