mapper.select:
SELECT COUNT(*)
  FROM tb_approval_mst A, tb_approval_dtl B, tb_orgnzt_manage C, tb_empl_manage D,
       tb_code E, tb_code F
 WHERE A.AM_CODE = B.AM_CODE
   AND A.OM_CODE = C.OM_CODE
   AND A.EM_NO = D.EM_NO
   AND A.AM_STATUS = E.C_CODE
   AND B.AD_TYPE = F.C_CODE
   AND A.DELETE_AT='N'



  AND (B.AD_DATE_FROM  BETWEEN #{params.ad_date_from} AND #{params.ad_date_to}
       OR B.AD_DATE_TO BETWEEN #{params.ad_date_from}  AND #{params.ad_date_to}) 



   AND A.AM_APPROVAL_DATE BETWEEN #{params.ad_date_from} and #{params.ad_date_to}




   AND C.CM_CODE =#{cm_code}


   AND A.OM_CODE =#{params.om_code}


   AND D.EM_NO = #{params.em_no}


   AND D.EM_NM LIKE '%${params.em_nm}%'


   AND A.AM_APPROVER_EM_NO = #{params.am_approver_em_no}


   AND A.AM_STATUS = #{params.am_status}


   AND B.AD_TYPE = #{params.approv_code}

 ORDER BY A.AM_APPROVAL_DATE DESC , A.AM_CODE DESC

${pagingStart} SELECT A.AM_CODE , A.OM_CODE , C.OM_NM , A.EM_NO , D.EM_NM , A.AM_APPROVER_EM_NO , A.AM_STATUS , E.C_NAME AS AM_STATUS_NM , B.AD_TYPE , F.C_NAME AS AD_TYPE_NM , DATE_FORMAT(A.AM_APPROVAL_DATE, '%Y-%m-%d') AS AM_APPROVAL_DATE , DATE_FORMAT(A.UPDT_DE, '%Y-%m-%d') AS UPDT_DE , A.DELETE_AT , ( SELECT COUNT(I.AI_NO) FROM tb_attach_main M , tb_attach_item I WHERE M.AM_NO = I.AM_NO AND M.DELETE_AT = 'N' AND M.AM_NO = B.AM_NO ) AS ATTACH_CNT , DATE_FORMAT(B.AD_DATE_FROM, '%Y-%m-%d') AS AD_DATE_FROM , B.AD_DATE_FROM_HHMM , DATE_FORMAT(B.AD_DATE_TO, '%Y-%m-%d') AS AD_DATE_TO , B.AD_DATE_TO_HHMM , CASE WHEN A.AM_STATUS = '0000000065' THEN ( SELECT DATE_FORMAT(U.UPDT_DE, '%Y-%m-%d') FROM tb_approval_reject U WHERE U.AR_SEQ = ( SELECT MAX(T.AR_SEQ) FROM tb_approval_reject T WHERE T.AM_CODE = A.AM_CODE ) ) WHEN A.AM_STATUS = '0000000391' THEN ( SELECT DATE_FORMAT(U.UPDT_DE, '%Y-%m-%d') FROM tb_approval_reject U WHERE U.AR_SEQ = ( SELECT MAX(T.AR_SEQ) FROM tb_approval_reject T WHERE T.AM_CODE = A.AM_CODE ) ) ELSE '' END AS REJECT_DATE FROM tb_approval_mst A, tb_approval_dtl B, tb_orgnzt_manage C, tb_empl_manage D, tb_code E, tb_code F WHERE A.AM_CODE = B.AM_CODE AND A.OM_CODE = C.OM_CODE AND A.EM_NO = D.EM_NO AND A.AM_STATUS = E.C_CODE AND B.AD_TYPE = F.C_CODE AND A.DELETE_AT='N'
AND (B.AD_DATE_FROM BETWEEN #{params.ad_date_from} AND #{params.ad_date_to} OR B.AD_DATE_TO BETWEEN #{params.ad_date_from} AND #{params.ad_date_to})
AND A.AM_APPROVAL_DATE BETWEEN #{params.ad_date_from} and #{params.ad_date_to}
AND C.CM_CODE =#{cm_code}
AND A.OM_CODE =#{params.om_code}
AND D.EM_NO = #{params.em_no}
AND D.EM_NM LIKE '%${params.em_nm}%'
AND A.AM_APPROVER_EM_NO = #{params.am_approver_em_no}
AND A.AM_STATUS IN ('0000000065', '0000000391')
AND A.AM_STATUS = #{params.am_status}
AND B.AD_TYPE = #{params.approv_code}
ORDER BY A.AM_APPROVAL_DATE DESC , A.AM_CODE DESC ${pagingEnd}

SELECT A.AM_CODE , A.OM_CODE , C.OM_NM , A.EM_NO , D.EM_NM , A.AM_APPROVER_EM_NO , A.AM_STATUS , E.C_NAME AS AM_STATUS_NM , B.AD_TYPE , F.C_NAME AS AD_TYPE_NM , DATE_FORMAT(A.AM_APPROVAL_DATE, '%Y-%m-%d') AS AM_APPROVAL_DATE , DATE_FORMAT(A.UPDT_DE, '%Y-%m-%d') AS UPDT_DE , A.DELETE_AT , ( SELECT COUNT(I.AI_NO) FROM tb_attach_main M , tb_attach_item I WHERE M.AM_NO = I.AM_NO AND M.DELETE_AT = 'N' AND M.AM_NO = B.AM_NO ) AS ATTACH_CNT , DATE_FORMAT(B.AD_DATE_FROM, '%Y-%m-%d') AS AD_DATE_FROM , B.AD_DATE_FROM_HHMM , DATE_FORMAT(B.AD_DATE_TO, '%Y-%m-%d') AS AD_DATE_TO , B.AD_DATE_TO_HHMM , CASE WHEN A.AM_STATUS = '0000000065' THEN ( SELECT DATE_FORMAT(U.UPDT_DE, '%Y-%m-%d') FROM tb_approval_reject U WHERE U.AR_SEQ = ( SELECT MAX(T.AR_SEQ) FROM tb_approval_reject T WHERE T.AM_CODE = A.AM_CODE ) ) WHEN A.AM_STATUS = '0000000391' THEN ( SELECT DATE_FORMAT(U.UPDT_DE, '%Y-%m-%d') FROM tb_approval_reject U WHERE U.AR_SEQ = ( SELECT MAX(T.AR_SEQ) FROM tb_approval_reject T WHERE T.AM_CODE = A.AM_CODE ) ) ELSE '' END AS REJECT_DATE FROM tb_approval_mst A, tb_approval_dtl B, tb_orgnzt_manage C, tb_empl_manage D, tb_code E, tb_code F WHERE A.AM_CODE = B.AM_CODE AND A.OM_CODE = C.OM_CODE AND A.EM_NO = D.EM_NO AND A.AM_STATUS = E.C_CODE AND B.AD_TYPE = F.C_CODE AND A.DELETE_AT='N'
AND (B.AD_DATE_FROM BETWEEN #{params.ad_date_from} AND #{params.ad_date_to} OR B.AD_DATE_TO BETWEEN #{params.ad_date_from} AND #{params.ad_date_to})
AND A.AM_APPROVAL_DATE BETWEEN #{params.ad_date_from} and #{params.ad_date_to}
AND C.CM_CODE =#{cm_code}
AND A.OM_CODE =#{params.om_code}
AND D.EM_NO = #{params.em_no}
AND D.EM_NM LIKE '%${params.em_nm}%'
AND A.AM_APPROVER_EM_NO = #{params.am_approver_em_no}
AND A.AM_STATUS = #{params.am_status}
AND B.AD_TYPE = #{params.approv_code}
ORDER BY A.AM_APPROVAL_DATE DESC , A.AM_CODE DESC

mapper.update:
{call SP_SAVE_30100(
      #{flag                 , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{am_code            , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{om_code            , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{em_no              , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{am_approver_em_no  , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{am_approval_date   , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{am_status          , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{ad_seq             , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{ad_type            , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{ad_date_from       , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{ad_date_from_hhmm  , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{ad_date_to         , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{ad_date_to_hhmm    , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{ad_reason          , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{am_no              , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{ar_seq             , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{ar_em_no           , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{ar_reason          , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{regist_man         , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{delete_at          , mode=IN, jdbcType=VARCHAR, javaType=string}
      , #{status_type        , mode=IN, jdbcType=VARCHAR, javaType=string}

      , #{res_am_code        , mode=OUT, jdbcType=VARCHAR, javaType=string}
      , #{res_code           , mode=OUT, jdbcType=VARCHAR, javaType=string}
      , #{res_msg            , mode=OUT, jdbcType=VARCHAR, javaType=string}
)}

UPDATE tb_approval_dtl
   SET AM_NO = #{am_no}
WHERE AM_CODE = #{am_code}
