select.if:
SELECT '' AS om_code
, '지점' om_nm
, '팀명' om_nm
, 0 om_sort_ordr FROM DUAL UNION ALL
SELECT om_code , om_nm , om_sort_ordr FROM tb_orgnzt_manage WHERE cm_code = #{cm_code} AND OM_CODE != '00006'
AND OM_CODE = #{om_code}
AND OM_CODE = #{tm_code}
AND OM_PARENT_NO = #{param_om_code}
AND om_orgnzt_se= #{om_orgnzt_se} AND use_at ='Y' AND delete_at ='N' ORDER BY om_sort_ordr

SELECT 'dw' AS om_code , '선택' om_nm , 0 om_sort_ordr FROM DUAL union all select om_code, om_nm, om_sort_ordr from tb_orgnzt_manage where 1=1 and cm_code = '002' and om_orgnzt_se = '1' and om_code in ('05213','05223')
SELECT '' AS om_code , '팀명' om_nm , 0 om_sort_ordr FROM DUAL union all select om_code, om_nm, om_sort_ordr from tb_orgnzt_manage where 1=1 and cm_code = '002' and om_orgnzt_se = '2' and OM_PARENT_NO = #{param_om_code}
ORDER BY om_sort_ordr
