mapper.select:
SELECT PM_CODE ,PM_PARENT_NO ,PM_NM ,PM_SE ,PM_DP ,PM_SORT_ORDR ,PM_MEMO ,USE_AT ,DELETE_AT ,REGIST_MAN ,REGIST_DE ,UPDT_MAN FROM tb_prdlst_manage WHERE PM_DP=#{pm_dp}
AND PM_PARENT_NO = #{pm_code}
AND USE_AT = 'Y' AND DELETE_AT ='N' ORDER BY PM_SORT_ORDR

mapper.update:
        UPDATE
            tb_prdlst_manage
        SET
			 PM_NM             = #{pm_nm}
			 ,PM_SORT_ORDR      = #{pm_sort_ordr}
			 ,PM_MEMO           = #{pm_memo}
			 ,USE_AT            = #{use_at}
			 ,DELETE_AT         = 'N'
			 ,UPDT_MAN          = #{updt_man}
			 ,UPDT_DE           = NOW()
        WHERE
            PM_CODE = #{pm_code}
